'use client'

import { useEffect } from 'react'
// import type { Metadata } from "next";
import "./globals.css"
import { QueryClientProvider, QueryClient } from "@tanstack/react-query"
import Header from '@/app/common/header/page'
import Footer from '@/app/common/footer/page'
import Modal from '@/app/modal'
import { modalUiStateAtom } from '@/store/ai'
import { useAtom } from 'jotai'

const queryClient = new QueryClient()

// export const metadata: Metadata = {
//   title: "Create Next App",
//   description: "Generated by create next app",
// };


export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {

  const [aiImodalUiStatenput, _] = useAtom(modalUiStateAtom);

  const shouldDisableScroll = Object.values(aiImodalUiStatenput).some((state) => state);

  useEffect(() => {
    document.body.style.overflow = shouldDisableScroll ? "hidden" : "auto";

    return () => {
      document.body.style.overflow = "auto"; // Cleanup
    };
  }, [shouldDisableScroll]);

  
  return (
    <QueryClientProvider client={queryClient}>
    <html lang="en">
      <body>
        {/* <div id="global-modal"></div> */}
        <Modal />
        <Header /> 
        {children}
        <Footer />
      </body>
    </html>
    </QueryClientProvider>
  );
}
